var t1 = ['t1_main','t1_graph','t1_info','t1_settings'];
var t2 = ['t2_creature','t2_environment','t2_spawn'];

function toolbarManager(side,tab) {

	if(side == 1) {
		
		for(var i = 0; i < t1.length; i++){
			
			document.getElementById(t1[i]).style.display = 'none';
			document.getElementById(t1[i]+'_btn').className = 'unselected';
			
		}
		
	} else {
		
		for(var i = 0; i < t2.length; i++){
			
			document.getElementById(t2[i]).style.display = 'none';
			document.getElementById(t2[i]+'_btn').className = 'unselected';
			
		}
		
	}
	
	document.getElementById(tab).style.display = 'inline';
	document.getElementById(tab+'_btn').className = 'selected';

}	


function selectedCreatureStats() {
	
	var c = game.selectedCreature;
	
	if(c == undefined) {
		
		c = game.blankCreature;
		
	}
	
	if(c.state == 'dead'){var deadCheck = ' (dead)'} else {var deadCheck = ''}
	
	document.getElementById('selected_creature_name').innerHTML = `<b>Name:</b> ${c.name}${deadCheck}`;
	document.getElementById('selected_creature_edit_name').placeholder = `${c.name}`;
	document.getElementById('selected_creature_age').innerHTML = `<b>Age:</b> ${(c.age/60).toFixed(1)} seconds (${parseFloat(c.agePenalty * 60).toFixed(1)} hp/s)`;
	document.getElementById('selected_creature_edit_age').innerHTML = `<b>Age:</b> ${(c.age/60).toFixed(1)} seconds (${parseFloat(c.agePenalty * 60).toFixed(1)} hp/s)`;
	document.getElementById('selected_creature_speed').innerHTML = `<b>Speed:</b> ${(c.speed * 3).toFixed(1)} m/s`;
	document.getElementById('selected_creature_edit_speed').placeholder = `${(c.speed * 3).toFixed(1)} m/s`;
	document.getElementById('selected_creature_sense').innerHTML = `<b>Sight:</b> ${(c.sense / 20).toFixed(1)} m`;
	document.getElementById('selected_creature_edit_sense').placeholder = `${(c.sense / 20).toFixed(1)} m`;
	document.getElementById('selected_creature_size').innerHTML = `<b>Size:</b> ${(c.size / 20 * 100 * 2).toFixed(1)} cm wide`;
	document.getElementById('selected_creature_edit_size').placeholder = `${(c.size / 20 * 100 * 2).toFixed(1)} cm wide`;
		
}

function editSelected() {
	
	var obj = document.getElementById('selected_creature_edit_name');
	if(obj.value != '') {game.selectedCreature.name = obj.value; obj.value = ''}
	var obj = document.getElementById('selected_creature_edit_speed');
	if(obj.value != '') {game.selectedCreature.speed = obj.value / 3; obj.value = ''}
	var obj = document.getElementById('selected_creature_edit_sense');
	if(obj.value != '') {game.selectedCreature.sense = obj.value * 20; obj.value = ''}
	var obj = document.getElementById('selected_creature_edit_size');
	if(obj.value != '') {game.selectedCreature.size = obj.value * 20 / 100 / 2; obj.value = ''}
}

function toggleShow(ids) {
	
	for(var i=0; i < ids.length; i++) {
		var obj = document.getElementById(ids[i]);
		if(obj.style.display != 'none') {obj.style.display = 'none'} else {obj.style.display = 'inline'}
	}
}


