	interval2 = setInterval(gatherData,1000 * game.graphRate);
	document.getElementById('graph_rate_lbl').innerHTML = `Data Update Time (${game.graphRate}s)`;
	
	game.dataCollectAmount = document.getElementById('data_limit').value;
